import React, { useState } from 'react';
import axios from 'axios';

const Chatbot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMsg = { sender: 'user', text: input };
    setMessages((prev) => [...prev, userMsg]);

    try {
      const res = await axios.post('http://localhost:5000/api/chatbot', {
        message: input,
      });
      const botMsg = { sender: 'bot', text: res.data.reply };
      setMessages((prev) => [...prev, botMsg]);
    } catch (err) {
      console.error('Error:', err);
      const botMsg = { sender: 'bot', text: '‚ùå Server is not responding' };
      setMessages((prev) => [...prev, botMsg]);
    }

    setInput('');
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-100 via-lime-200 to-yellow-100 flex items-center justify-center p-4 font-sans">
      <div className="w-full max-w-md bg-white border border-green-300 shadow-2xl rounded-2xl p-6">
        <h2 className="text-2xl font-extrabold text-green-700 mb-4 text-center flex items-center justify-center gap-2">
          üë©‚Äçüåæ AgroBot üë©‚Äçüåæ <br /> Your Farming Buddy üåæ
        </h2>

        <div className="h-80 overflow-y-auto mb-4 space-y-2 pr-2 scrollbar-thin scrollbar-thumb-green-400 scrollbar-track-green-100">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`px-4 py-2 rounded-xl max-w-xs text-sm shadow-md transition-all duration-200 ${
                  msg.sender === 'user'
                    ? 'bg-green-500 text-white rounded-br-none'
                    : 'bg-yellow-100 text-gray-900 rounded-bl-none border border-green-200'
                }`}
              >
                {msg.text}
              </div>
            </div>
          ))}
        </div>

        <div className="flex gap-2">
          <input
            type="text"
            className="flex-1 border border-green-400 px-3 py-2 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500 bg-green-50 placeholder:text-green-700"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Ask about crops, weather, tools..."
            onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
          />
          <button
            onClick={sendMessage}
            className="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 transition"
          >
            üöú Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
